package week1.day3;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int version1 =+10;
		int version2 =-10;
		
		if( version1>=0)
			System.out.println(version1 +" is a positive no");
		else
			System.out.println(version1 +" is a negative no");
		
		if( version2>=0)
			System.out.println(version2 +" is a positive no");
		else
			System.out.println(version2 +" is a negative no");
	}

}
